        public override bool GensBool { get => LakeGen; set => LakeGen = value; }
        public override string NameGen => "[Synergia] Lake in hell";
        public override bool Do_MakeGen(GenerationProgress progress) {
            if (progress != null) {
                progress.Message = "";
                progress.Set(0.99f);
            }

            int startX = Main.maxTilesX - 1036; // <--- До какой точки X будет очистка
            int startY = Main.maxTilesY - 44; // <--- Самая нижния правая точка в аду по Y

            int EndX = Main.maxTilesX - 800; // <--- Самая нижния правая точка в аду по X с которой начнетса генерация
            int EndY = Main.maxTilesY - 162; // <--- До какой точки Y будет очистка

            HellLakeX = EndX;
            HellLakeY = startY;

            int width = LakeTile.GetLength(1);
            int height = LakeTile.GetLength(0);

            WorldHelper.Cleaning(EndX, startY, startX, EndY, TileID.Ash, TileID.Hellstone, ModContent.TileType<Sinstone>(), TileID.AshGrass, TileID.Stone, 82, 637, 185, 634, 28);

            for (int y = 0; y < height; y++) {
                for (int x = 0; x < width; x++) {
                    int worldX = HellLakeX - 236 + x;
                    int worldY = HellLakeY - y;

                    if (!WorldGen.InWorld(worldX, worldY, 10)) {
                        continue;
                    }

                    Tile tile = Framing.GetTileSafely(worldX, worldY);
                    tile.ClearEverything();

                    switch (LakeTile[y, x]) {
                        case 0: break;
                        case 1: tile.TileType = (ushort)TileType<Sinstone>(); tile.HasTile = true; break;
                        case Q: tile.LiquidType = LiquidID.Lava; tile.LiquidAmount = 255; break;
                    }
                    switch (LakeSlope[y, x]) {
                        case 0: break;
                        case 1: tile.IsHalfBlock = true; break;
                        case 2: tile.Slope = SlopeType.SlopeDownRight; break;
                        case 3: tile.Slope = SlopeType.SlopeUpLeft; break;
                        case 4: tile.Slope = SlopeType.SlopeUpRight; break;
                        case 5: tile.Slope = SlopeType.SlopeDownLeft; break;
                    }
                }
            }
            return true;
        }
    }
}
