        public static bool GenHeadquarter(GenerationProgress progress) {
            if (progress != null) {
                progress.Message = "Localization";
                progress.Set(0.33f);
            }

            HLOX = SwampCenterX;
            int baseY = (int)Main.rockLayer;
            for (int i = 0; i < 1000;) { // TODO: 1000 возможно мало для большого мира
                if (Main.tile[HLOX, baseY - i].TileType != TileType<SwampMud>()) { i++; }
                else { HLOY = baseY - i; break; }
            }

            WorldHelper.CleaningAll(HLOX + 85, HLOY - 109, HLOX - 86, HLOY - 30);

            int width = HeadquarterLayerOneTiles.GetLength(1);
            int height = HeadquarterLayerOneTiles.GetLength(0);

            for (int y = 0; y < height; y++) {
                for (int x = 0; x < width; x++) {
                    int worldX = HLOX - 86 + x;
                    int worldY = HLOY - 30 - y;

                    if (!WorldGen.InWorld(worldX, worldY, 10)) { continue; }

                    Tile tile = Framing.GetTileSafely(worldX, worldY);
                    tile.ClearEverything();

                    switch (HeadquarterLayerOneTiles[y, x]) {
                        case 0: break;
                        case 1: tile.TileType = (ushort)TileType<SwampMud>(); tile.HasTile = true; break;
                        case 2: tile.TileType = TileID.IronBrick; tile.HasTile = true; break;
                        case 3: tile.TileType = TileID.StoneSlab; tile.HasTile = true; break;
                        case 4: tile.TileType = (ushort)TileType<PeatBlock>(); tile.HasTile = true; break;
                        case 5: WorldGen.PlaceTile(worldX, worldY, TileType<CoughwoodPlatform>()); break;
                        case 6: tile.TileType = TileID.IronBrick; tile.HasTile = true; tile.IsActuated = true; break;
                        case 7: tile.TileType = (ushort)TileType<SwampWood>(); tile.HasTile = true; break;
                    }
                    switch (HeadquarterLayerOneWalls[y, x]) {
                        case 0: break;
                        case 1: tile.WallType = (ushort)WallType<SwampWall>(); break;
                        case 2: tile.WallType = WallID.StoneSlab; break;
                        case 3: tile.WallType = WallID.IronBrick; break;
                        case 4: tile.WallType = WallID.Lavafall; break;
                        case 5: tile.WallType = WallID.Cave2Unsafe; break;
                        case 6: tile.WallType = (ushort)WallType<SwampWoodWall>(); break;
                        case 7: tile.WallType = (ushort)WallType<CoughwoodFence>(); break;
                        case 8: tile.WallType = (ushort)WallType<CoughwoodWall>(); break;
                    }
                    switch (HeadquarterLayerOneSlope[y, x]) {
                        case 0: break;
                        case 1: tile.IsHalfBlock = true; break;
                        case 2: tile.Slope = SlopeType.SlopeDownRight; break;
                        case 3: tile.Slope = SlopeType.SlopeUpLeft; break;
                        case 4: tile.Slope = SlopeType.SlopeUpRight; break;
                        case 5: tile.Slope = SlopeType.SlopeDownLeft; break;
                    }
                    switch (HeadquarterLayerOneLiquid[y, x]) {
                        case 0: tile.LiquidAmount = 0; break;
                        case 1: tile.LiquidType = LiquidID.Lava; tile.LiquidAmount = 255; break;
                    }
                }
            }
            return true; // HeadquarterLayerTwo.GenHeadquarter(progress) // GenerationProgress
        }
        public static void SpawnLava() {
            int width = HeadquarterLayerOneTiles.GetLength(1);
            int height = HeadquarterLayerOneTiles.GetLength(0);

            for (int y = 0; y < height; y++) {
                for (int x = 0; x < width; x++) {
                    int worldX = HLOX - 86 + x;
                    int worldY = HLOY - 30 - y;

                    if (!WorldGen.InWorld(worldX, worldY, 10)) { continue; }

                    Tile tile = Framing.GetTileSafely(worldX, worldY);

                    switch (HeadquarterLayerOneLiquid[y, x]) {
                        case 0: tile.LiquidAmount = 0; break;
                        case 1: tile.LiquidType = LiquidID.Lava; tile.LiquidAmount = 255; break;
                    }
                }
            }
        }
    }
